import React, { useState } from 'react';
import Slider from 'react-slider';
import './styles.css';

const PriceSlider = ({ name,value, changeDiff }) => {

  const [values, setValues] = useState([0, 100]);
  const handleChange = (newValues) => setValues(newValues);
  return (
    <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '5px', boxShadow: '0 0 5px rgba(0, 0, 0, 0.1)' }}>

      <Slider
        className="slider"
        value={value}
        onChange={changeDiff}
        min={0}
        max={10}
      />
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <div>
          <label htmlFor="minPrice">Min {name}:</label>
          <input
            type="number"
            id="minPrice"
            value={value[0]}
            onChange={(e) => handleChange([+e.target.value, value[1]])}
          />
        </div>
        <div>
          <label htmlFor="maxPrice">Max {name}:</label>
          <input
            type="number"
            id="maxPrice"
            value={value[1]}
            onChange={(e) => handleChange([value[0], +e.target.value])}
          />
        </div>
      </div>
    </div>
  );
};

export default PriceSlider;
