import React, { useState } from 'react';

export const SliderProton = ({ min, max, onChange }) => {
  const [minVal, setMinVal] = useState(min);
  const [maxVal, setMaxVal] = useState(max);

  const updateRange = (val, type) => {
    if (type === 'min' && val < maxVal) {
      setMinVal(val);
      onChange([val, maxVal]);
    } else if (type === 'max' && val > minVal) {
      setMaxVal(val);
      onChange([minVal, val]);
    }
  };

  return (
    <div>
      <input
        type="range"
        min={min}
        max={max}
        value={minVal}
        onChange={(e) => updateRange(Number(e.target.value), 'min')}
      />
      <input
        type="range"
        min={min}
        max={max}
        value={maxVal}
        onChange={(e) => updateRange(Number(e.target.value), 'max')}
      />
      <div>
        Range: {minVal} - {maxVal}
      </div>
    </div>
  );
};
