import React from 'react';
import './styles.css';


import {Link} from "react-router-dom";
const ListItem = ({

  item: { recipe_id,recipe_name,ratinglvl, level_id, recipe_view, type_id, nation_id,ingredient ,photo,isvegan,isvegetarian},
                      type_receipts, nations, ingList,
}) => (
  <div className='listItem-wrap'>
      <img src={photo} alt='' />

          <Link to={`/receipt/${recipe_id}`}>
           {recipe_name}
          </Link>

      <span>‚≠ê{ratinglvl}</span>

      <p>

          <b>{type_receipts.find(x => x.type_id === type_id).type_name}, {nations.find(x => x.nation_id === nation_id).nation_name}</b>

      </p>
      <p>
          <b>{!isvegan ? "" : "/ Vegan"}</b>
          <b>{!isvegetarian ? "" : "/ Vegetarian"}</b>
      </p>
      <p>
          <b> {ingredient.map((element) => {
             return ingList.find(x => x.id === element).name+ " "
          })
          }
          </b>

      </p>
    <footer>


      <p>
        <b>Level of Difficulty: {level_id} </b>
          <b> üëÄ{recipe_view}</b>
      </p>

    </footer>
  </div>
);

export default ListItem;
