import React, { useEffect, useState } from "react";
import { veganList } from '../../../constants';
import {CheckboxProton,CheckboxProtonNation} from '../CheckboxProton';
/*import FilterListToggle from '../../common/FilterListToggle';
import {SliderProtonDiff, SliderProtonRat, SliderProtonView} from '../../common/SliderProtonView';
import CheckboxesTags from '../../common/TagInputAutoSuggest';*/

import './styles.css';

const FilterPanel = ({
  selectedVegan,
  selectVegan,
  type_receipts,
  changeCheckedType,
  nations,
  changeCheckedNation,
  selectedDiff,
  changeDiff,
  selectedView,
  changeView,
  selectedRating,
  changeRating,
  nulifyVegan,
                         ingList,
                         changeIngre,
                         ing1List,
})  => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
  <div>

    {/*Type*/}
    <div className='input-group'>

    <div className='label' onClick={toggleDropdown}>Type</div>
      {type_receipts.map((typeofDish) => (
        <CheckboxProton
          key={typeofDish.type_id}
          id={typeofDish.type_id}
          name={typeofDish.type_name}
          checked={typeofDish.checked}
          changeCheckedType={changeCheckedType}
        />
      ))}
    </div>



  </div>

)};

export default FilterPanel;
