import React, { useState, useEffect } from 'react';
import './style.css';

const TagInputAutoSuggest = ({ options, value, onChange }) => {
  const [inputValue, setInputValue] = useState('');
  const [suggestedTags, setSuggestedTags] = useState([]);

  useEffect(() => {
    setSuggestedTags(options.filter(tag => !value.includes(tag)));
  }, [options, value]);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
    setSuggestedTags(options.filter(tag => tag.toLowerCase().includes(event.target.value.toLowerCase()) && !value.includes(tag)));
  };

  const handleAddTag = (tag) => {
    onChange([...value, tag]);
    setInputValue('');
  };

  const handleDeleteTag = (tagToDelete) => {
    const updatedTags = value.filter((tag) => tag !== tagToDelete);
    onChange(updatedTags);
  };

  return (
    <div>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        placeholder="Add Tags"
      />
      <div className="suggested-tags">
        {suggestedTags.map((tag) => (
          <div key={tag} className="tag" onClick={() => handleAddTag(tag)}>
            {tag}
          </div>
        ))}
      </div>
      <div className="selected-tags">
        {value.map((tag) => (
          <div key={tag} className="tag">
            {tag}
            <span className="tag-close" onClick={() => handleDeleteTag(tag)}>
              &#10005;
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TagInputAutoSuggest;
