import React, { useState } from 'react';
import './style.css';

const TagInputAutoSuggest = ({ tags, selectedTags, onChange }) => {
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleTagClick = (tag) => {
    if (!selectedTags.includes(tag)) {
      onChange([...selectedTags, tag]);
    }
    setInputValue('');
  };

  const handleRemoveTag = (tag) => {
    const updatedTags = selectedTags.filter((t) => t !== tag);
    onChange(updatedTags);
  };

  const filteredTags = tags.filter(
    (tag) => tag.name.toLowerCase().includes(inputValue.toLowerCase())
  );

  return (
    <div>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        placeholder="Type to search and add tags"
      />
      <ul>
        {filteredTags.map((tag) => (
          <li key={tag.id} onClick={() => handleTagClick(tag)}>
            {tag.name}
          </li>
        ))}
      </ul>
      <div>
        {selectedTags.map((tag) => (
          <span key={tag.id} onClick={() => handleRemoveTag(tag)}>
            {tag.name} &times;
          </span>
        ))}
      </div>
    </div>
  );
};

export default TagInputAutoSuggest;
