import React from 'react';
import './styles.css'; // Assuming you move your styles to a CSS file

const FilterListToggle = ({ options, value, selectToggle }) => {
  console.log(value)
  return (
    <div className="toggle-button-group">
      {options.map(({ label, id, value: optionValue }) => (

        <button
          key={id}
          className={`toggle-button ${value === optionValue ? 'selected' : ''}`}
          onClick={() => selectToggle(optionValue)}
        >
          {label}
        </button>
      ))
      }
    </div>
  );
};

export default FilterListToggle;
