import React, { useState } from "react";
import { CheckboxProton, CheckboxProtonNation } from '../CheckboxProton';
import './styles.css';

const FilterPanel = ({
  selectedVegan,
  selectVegan,
  type_receipts,
  changeCheckedType,
  nations,
  changeCheckedNation,
  selectedDiff,
  changeDiff,
  selectedView,
  changeView,
  selectedRating,
  changeRating,
  nulifyVegan,
  ingList,
  changeIngre,
  ing1List,
}) => {
  const [typeDropdownOpen, setTypeDropdownOpen] = useState(false);
  const [nationDropdownOpen, setNationDropdownOpen] = useState(false);
  const [filterOption, setFilterOption] = useState("both");

  const toggleTypeDropdown = () => {
    setTypeDropdownOpen(!typeDropdownOpen);
  };

  const toggleNationDropdown = () => {
    setNationDropdownOpen(!nationDropdownOpen);
  };

  const handleFilterChange = (option) => {
    setFilterOption(option);
  };

  return (
    <div>
      {/* Type */}
      <div className='input-group'>
        <div className='label' onClick={toggleTypeDropdown}>Type</div>
        {typeDropdownOpen && (
          <div className="checkbox-container">
            {type_receipts.map((typeofDish) => (
              <CheckboxProton
                key={typeofDish.type_id}
                id={typeofDish.type_id}
                name={typeofDish.type_name}
                checked={typeofDish.checked}
                changeChecked={changeCheckedType}
              />
            ))}
          </div>
        )}
      </div>

      {/* Nation */}
      <div className='input-group'>
        <div className='label' onClick={toggleNationDropdown}>Nation</div>
        {nationDropdownOpen && (
          <div className="checkbox-container">
            {nations.map((nation) => (
              <CheckboxProton
                key={nation.nation_id}
                id={nation.nation_id}
                name={nation.nation_name}
                checked={nation.checked}
                changeChecked={changeCheckedNation}
              />
            ))}
          </div>
        )}
      </div>

      {/* Filter Options */}
      <div className='input-group'>
        <div className='label'>Filter</div>
        <div className='filter-options'>
          <label>
            <input
              type="radio"
              value="vegan"
              checked={filterOption === "vegan"}
              onChange={() => handleFilterChange("vegan")}
            />
            Vegan
          </label>
          <label>
            <input
              type="radio"
              value="vegetarian"
              checked={filterOption === "vegetarian"}
              onChange={() => handleFilterChange("vegetarian")}
            />
            Vegetarian
          </label>
          <label>
            <input
              type="radio"
              value="both"
              checked={filterOption === "both"}
              onChange={() => handleFilterChange("both")}
            />
            Both
          </label>
          <label>
            <input
              type="radio"
              value="none"
              checked={filterOption === "none"}
              onChange={() => handleFilterChange("none")}
            />
            None
          </label>
        </div>
      </div>
    </div>
  );
};

export default FilterPanel;
