import React from 'react';

// Inline styles - You can also move these to a separate CSS file
const styles = {
  root: {
    width: '100%',
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: '1rem',
  },
  toggleButton: {
    fontFamily: `'Raleway', sans-serif`,
    fontSize: '.8rem',
    border: '1px solid rgba(0, 0, 0, 0.12)',
    borderRadius: '10px',
    padding: '0.5rem 1rem',
    margin: '0 0.5rem',
    backgroundColor: '#fff',
    color: '#000',
    cursor: 'pointer',
  },
  selected: {
    backgroundColor: '#000',
    color: '#fff',
  },
};

const FilterListToggle = ({ options, value, selectToggle }) => {
  // Function to handle toggle click and pass the value to selectToggle
  const handleToggleClick = (val) => () => {
    selectToggle(val);
  };

  return (
    <div style={styles.root}>
      {options.map(({ label, id, val }) => (
        <button
          key={id}
          style={{
            ...styles.toggleButton,
            ...(value === val ? styles.selected : {}),
          }}
          onClick={handleToggleClick(val)}
        >
          {label}
        </button>
      ))}
    </div>
  );
};

export default FilterListToggle;
