import React from 'react';
import './style.css';
import {Link} from "react-router-dom";


const RecipePage = ({ list, type_receipts,nations, ingList}) => (

    <div className='listItem-wrap'>
        <img src={list.photo} alt='' />

        <header>
            <Link to={`/receipt/${list.recept_id}`}>
                {list.recept_name}
            </Link>

            <span>{list.ratinglvl}</span>
        </header>
        <p>

            <b>{type_receipts.find(x => x.type_id === list.type_id).type_name}, {nations.find(x => x.nation_id === list.nation_id).nation_name}</b>

        </p>
        <p>
            <b>{!list.isvegan ? "" : "/ Vegan"}</b>
            <b>{!list.isvegetarian ? "" : "/ Vegetarian"}</b>
        </p>
        <p>
            <b> {list.ingredient.map((element) => {
                return ingList.find(x => x.id === element).name+ " "
            })
            }
            </b>

        </p>
        <footer>


            <p>
                <b>Level of Difficulty: {list.level_id} </b>
                <b>  {list.recept_view}</b>
            </p>

        </footer>
    </div>
);

export default RecipePage;
