import React, { useState } from "react";
import { CheckboxProton } from '../CheckboxProton';
import './styles.css';

const FilterPanel = ({
  selectedVegan,
  selectVegan,
  type_receipts,
  changeCheckedType,
  nations,
  changeCheckedNation,
  selectedDiff,
  changeDiff,
  selectedView,
  changeView,
  selectedRating,
  changeRating,
  nulifyVegan,
  ingList,
  changeIngre,
  ing1List,
}) => {
  const [typeDropdownOpen, setTypeDropdownOpen] = useState(false);
  const [nationDropdownOpen, setNationDropdownOpen] = useState(false);
  const [filterOption, setFilterOption] = useState("both");

  const toggleTypeDropdown = () => {
    setTypeDropdownOpen(!typeDropdownOpen);
  };

  const toggleNationDropdown = () => {
    setNationDropdownOpen(!nationDropdownOpen);
  };

  const handleToggleFilter = () => {
    const filterOptions = ["vegan", "vegetarian", "both", "none"];
    const currentIndex = filterOptions.indexOf(filterOption);
    const nextIndex = (currentIndex + 1) % filterOptions.length;
    setFilterOption(filterOptions[nextIndex]);
  };

  return (
    <div>
      {/* Type */}
      <div className='input-group'>
        <div className='label' onClick={toggleTypeDropdown}>Type</div>
        {typeDropdownOpen && (
          <div className="checkbox-container">
            {type_receipts.map((typeofDish) => (
              <CheckboxProton
                key={typeofDish.type_id}
                id={typeofDish.type_id}
                name={typeofDish.type_name}
                checked={typeofDish.checked}
                changeChecked={changeCheckedType}
              />
            ))}
          </div>
        )}
      </div>

      {/* Nation */}
      <div className='input-group'>
        <div className='label' onClick={toggleNationDropdown}>Nation</div>
        {nationDropdownOpen && (
          <div className="checkbox-container">
            {nations.map((nation) => (
              <CheckboxProton
                key={nation.nation_id}
                id={nation.nation_id}
                name={nation.nation_name}
                checked={nation.checked}
                changeChecked={changeCheckedNation}
              />
            ))}
          </div>
        )}
      </div>

      {/* Toggle Filter Button */}
      <div className='input-group'>
        <button onClick={handleToggleFilter}>Toggle Filter</button>
        <div className='filter-option'>{`Filter: ${filterOption}`}</div>
      </div>
    </div>
  );
};

export default FilterPanel;
