import React, { useState } from 'react';

const TagInputAutoSuggest = ({ suggestions, selectedTags, onChange }) => {
    const [inputValue, setInputValue] = useState('');
  
    const handleInputChange = (event) => {
      setInputValue(event.target.value);
    };
  
    const handleSelectTag = (selectedTag) => {
      setInputValue('');
      onChange([...selectedTags, selectedTag]);
    };
  
    const handleRemoveTag = (tagToRemove) => {
      const updatedTags = selectedTags.filter((tag) => tag !== tagToRemove);
      onChange(updatedTags);
    };
    const filteredSuggestions = suggestions.filter(
        (suggestion) =>
          suggestion.name.toLowerCase().includes(inputValue.toLowerCase()) &&
          !selectedTags.includes(suggestion.id)
          
      );
      console.log(filteredSuggestions)

  
    return (
      <div>
        <div>
          {selectedTags.map((tag) => (
            <span key={tag.id} className="selected-tag">
              {tag.name}
              <button onClick={() => handleRemoveTag(tag)}>&times;</button>
            </span>
          ))}
        </div>
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Type to search tags"
        />
        <ul>
          {filteredSuggestions.map((suggestion) => (
            <li key={suggestion.id} onClick={() => handleSelectTag(suggestion)}>
              {suggestion.name}
            </li>
          ))}
        </ul>
      </div>
    );
  };
export default TagInputAutoSuggest;
